CREATE DATABASE IF NOT EXISTS onlinestore;

USE onlinestore;

CREATE TABLE IF NOT EXISTS users (
  uId INT(20) NOT NULL AUTO_INCREMENT,
  type INT(1) NOT NULL,
  userName VARCHAR(20) NOT NULL,
  password VARCHAR(200) NOT NULL,
  PRIMARY KEY (uId)
);

CREATE TABLE IF NOT EXISTS customer(
  cId INT(20) NOT NULL,
  cName VARCHAR (200) NOT NULL,
  name VARCHAR (200) NOT NULL,
  surname VARCHAR(20) NOT NULL,
  password VARCHAR(200) NOT NULL,
  address VARCHAR(200) NOT NULL,
  email VARCHAR(320) NOT NULL,
  PRIMARY KEY (cId),
  CONSTRAINT FOREIGN KEY (cId) REFERENCES users (uId) ON DELETE CASCADE ON UPDATE CASCADE
);


CREATE TABLE IF NOT EXISTS product(
  pId INT (20) NOT NULL AUTO_INCREMENT,
  type varchar (10) NOT NULL,
  pName VARCHAR(200) NOT NULL,
  price FLOAT(11) NOT NULL,
  stock INT(11) NOT NULL,
  image VARCHAR(500) NOT NULL,
  purchasedPrice FLOAT(11) NOT NULL,
  sellingPrice FLOAT(11),
  discount INT(11) NOT NULL,
  description VARCHAR(2000) NOT NULL,
  publisher VARCHAR(30) NOT NULL,
  publishedDate DATE,
  avgRate FLOAT(10),
  PRIMARY KEY (pId)
);


CREATE TABLE IF NOT EXISTS shoppingCart (
  cId INT (20) NOT NULL,
  pId INT (20) NOT NULL,
  pName VARCHAR(100),
  quantity INT (11) NOT NULL,
  price FLOAT (11),
  image VARCHAR(300),
  discounted FLOAT (11),
  PRIMARY KEY (cId,pId),
  CONSTRAINT FOREIGN KEY (cId) REFERENCES users (uId) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT FOREIGN KEY (pId) REFERENCES product (pId) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS delivery (
  dId INT (20) NOT NULL,
  cId INT (20) NOT NULL,
  pId INT (20) NOT NULL,
  pName VARCHAR (200),
  dDate DATE NOT NULL,
  quantity INT (11) NOT NULL,
  price float (11),
  isDelivered bit(1),
  PRIMARY KEY (dId, cId, pId),
  CONSTRAINT FOREIGN KEY (cId) REFERENCES customer (cId) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT FOREIGN KEY (pId) REFERENCES product (pId) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS category (
  catId INT (20) NOT NULL AUTO_INCREMENT,
  catName VARCHAR(30) NOT NULL,
  type VARCHAR(10) NOT NULL,
  PRIMARY KEY (catId)
);

CREATE TABLE IF NOT EXISTS product_category (
  pId INT (20) NOT NULL,
  catId INT (20) NOT NULL,
  PRIMARY KEY (pID, catId),
  CONSTRAINT FOREIGN KEY (pId) REFERENCES product (pId) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT FOREIGN KEY (catId) REFERENCES category (catId) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS review (
  rId INT(20) NOT NULL AUTO_INCREMENT,
  cId INT (20) NOT NULL,
  pId INT (20) NOT NULL,
  cName VARCHAR(40) NOT NULL,
  rate INT (5) NOT NULL,
  comment VARCHAR (500),
  rDate DATE NOT NULL,
  isApproved BIT(1),
  PRIMARY KEY (rId),
  CONSTRAINT FOREIGN KEY (cId) REFERENCES customer (cId) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT FOREIGN KEY (pId) REFERENCES product (pId) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS campaign (
  camId INT (20) NOT NULL AUTO_INCREMENT,
  pId INT (20) NOT NULL,
  discount INT (11) NOT NULL,
  code INT (8) NOT NULL,
  isValid bit(1),
  location VARCHAR(30),
  PRIMARY KEY (camId),
  CONSTRAINT FOREIGN KEY (pId) REFERENCES product (pId) ON DELETE CASCADE ON UPDATE CASCADE
);

